import { useEffect, useReducer, useRef, useState } from "react";
import TodoCreate from "./TodoCreate";
import TodoList from "./TodoList";
import axios from "axios";
import { Outlet } from "react-router-dom";

function reducer(state, action) {
  switch (action.type) {
    case "create":
      return [...state, { id: action.id, text: action.text, done: false }];
    case "remove":
      return state.filter((todo) => todo.id !== action.id);
    case "toggle":
      return state.map((todo) =>
        todo.id === action.id ? { ...todo, done: !todo.done } : todo
      );
    default:
      return state;
  }
}

const Data = () => {
  const [posts, setPosts] = useState({
    isLoading: true,
    data: null,
    isError: false,
  });
  const fetchData = async () => {
    const { data } = await axios.get("http://localhost:5000/posts");
    setPosts({
      isLoading: false,
      data,
      isError: false,
    });
  };
  useEffect(() => {
    fetchData();
  }, []);
  console.log("데이터: ");
  console.log(posts.data);
  return <></>;
};

function Todos() {
  // json server start
  const [posts, setPosts] = useState({
    isLoading: true,
    data: null,
    isError: false,
  });
  const fetchData = async () => {
    const { data } = await axios.get("http://localhost:5000/posts");
    setPosts({
      isLoading: false,
      data,
      isError: false,
    });
  };

  useEffect(() => {
    fetchData();
  }, []);
  // json server end

  useEffect(() => {
    if (posts.data) {
      posts.data.forEach((todo) => {
        dispatch({ type: "create", id: todo.id, text: todo.text });
      });
    }

  }, [posts.data]);

  const [todos, dispatch] = useReducer(reducer, []);
  const [text, setText] = useState("");

  const handleText = (e) => setText(e.target.value);
  const nextId = useRef();

  // 추가
  const handleSubmit = async (e) => {
    e.preventDefault();
    const newTodo = { id: nextId.current, text, done: false };
    const { data } = await axios.post("http://localhost:5000/posts", newTodo);
    dispatch({ type: "create", id: data.id, text: data.text })
    setText(""); // 입력창 초기화
  };


  // 삭제
  const handleRemove = async (id) => {
    if (window.confirm("delete ok?")) {
      try {
        await axios.post(`http://localhost:5000/post/delete/${id}`);
        dispatch({ type: "remove", id });
      } catch (err) {
        console.error("삭제 실패:", err);
      }
    }
  };


  // 토글(밑줄)
  const handleToggle = (id) => dispatch({ type: "toggle", id });

  return (
    <div>
      <Data />
      <TodoList todos={todos} onRemove={handleRemove} onToggle={handleToggle} />
      <TodoCreate onChange={handleText} onSubmit={handleSubmit} />
      <Outlet context={{ posts }} />
    </div>
  );
}

export default Todos;
